<template>
  <div>
    <div id="header_div_ewrqwluevhtnwerl">
      <div id="span_body_div_erwevsdcwswerwer">
        <span id="span_ewrlweovc9xuwerw">
          <span src="" alt="" style="margin-left: 120px"/>
          <img src="data:image/svg+xml;base64,"
               @click="backYearClick">
          <img src="data:image/svg+xml;base64,"
              @click="nextYearClick">
          <img src="data:image/svg+xml;base64,"
              @click="backMonthClick">
          <img src="data:image/svg+xml;base64,"
               @click="nextMonthClick">
          <img src="data:image/svg+xml;base64,"
               @click="specificDate">
          <img src="data:image/svg+xml;base64,"
               alt="添加日程" @click="add_record">
        </span>
      </div>
      <ToSpecificDate @skip_date="deal_skip_date"/>
      <MonthView :user_auth_data_of_father="user_auth_data_of_father" :today_of_father="today_of_father" :cur_date_of_father="cur_date"
                 :host_of_father="host_of_father" @view_record="deal_view_record" ref="monthview"
              @cur_day="deal_cur_day" @skip_date="deal_skip_date"
      />
    </div>

    <!--    日程显示-->
    <RecordView id="record_view_olrucvw" style="display: none" :record_of_father="record" @cancel_view="deal_cancel_view"
        :user_auth_data_of_father="user_auth_data_of_father" :host_of_father="host_of_father"
        @skip_date="deal_skip_date" @delete_success="load_month_view"
    />

    <AddRecord id="add_record_olrucvw" style="display: none" @cancel_add_record="deal_cancel_add_record"
               :user_auth_data_of_father="user_auth_data_of_father" :host_of_father="host_of_father"
               @skip_date="deal_skip_date" @add_success="load_month_view"
    />

  </div>
</template>

<script>
import MonthView from "./MonthView.vue";
import ToSpecificDate from "./ToSpecificDate.vue";
import RecordView from "./RecordView.vue";
import AddRecord from "./AddRecord.vue";
export default {
  name: "Body",
  props:{
    user_auth_data_of_father: {
    },
    today_of_father: {
    },
    cur_date: {
    },
    host_of_father: "",
  },
  data(){
    return {
      record: {}
    }
  },
  components: {
    MonthView,ToSpecificDate,RecordView,AddRecord
  },
  methods: {
    add_record: function (){
      let el1 = document.getElementById("record_view_olrucvw");
      el1.style.display="none";
      let el2 = document.getElementById("header_div_ewrqwluevhtnwerl");
      el2.style.display="none";
      let el3 = document.getElementById("add_record_olrucvw");
      el3.style.display="block";
    },
    deal_cur_day: function (data){
      if (data.cur_day!==undefined){
        let data0 = {
          cur_day: data.cur_day,
        }
        this.$emit("cur_day",data0);
      }
    },
    load_month_view: function (){
      this.$refs.monthview.dealMonthViewRequest();
      this.deal_cancel_add_record();
      this.deal_cancel_view();
    },
    deal_cancel_view: function (){
        let el1 = document.getElementById("record_view_olrucvw");
        el1.style.display="none";
        let el2 = document.getElementById("header_div_ewrqwluevhtnwerl");
        el2.style.display="block";
    },
    deal_cancel_add_record: function (){
        let el1 = document.getElementById("add_record_olrucvw");
        el1.style.display="none";
        let el2 = document.getElementById("header_div_ewrqwluevhtnwerl");
        el2.style.display="block";
    },
    deal_view_record: function (data){
      if (data.record!==undefined){
        this.record=data.record;
        let el1 = document.getElementById("record_view_olrucvw");
        el1.style.display="block";
        let el2 = document.getElementById("header_div_ewrqwluevhtnwerl");
        el2.style.display="none";
      }
    },
    deal_skip_date: function (data){
      if (data.skip_date!==undefined){
        let a_data = {
          skip_date: data.skip_date
        }
        this.$emit("skip_date",a_data);
      }
    },
    backYearClick: function () {
      let cur_date_wervs = this.cur_date;
      cur_date_wervs.year=parseInt(cur_date_wervs.year)-1;
      this.$refs.monthview.dealMonthViewRequest();
      let a_data = {
        skip_date: {
          year: cur_date_wervs.year,
          month:cur_date_wervs.month,
          day: 1
        }
      }
      this.$emit("skip_date",a_data);
    },
    nextYearClick: function () {
      let cur_date_wervs = this.cur_date;
      cur_date_wervs.year=parseInt(cur_date_wervs.year)+1;
      this.$refs.monthview.dealMonthViewRequest();
      let a_data = {
        skip_date: {
          year: cur_date_wervs.year,
          month:cur_date_wervs.month,
          day: 1
        }
      }
      this.$emit("skip_date",a_data);
    },
    backMonthClick: function () {
      let cur_date_wervs = this.cur_date;
      cur_date_wervs.month= parseInt(cur_date_wervs.month)-1;
      if (cur_date_wervs.month === 0){
        cur_date_wervs.month=12;
        cur_date_wervs.year=cur_date_wervs.year-1;
      }
      this.$refs.monthview.dealMonthViewRequest();
      let a_data = {
        skip_date: {
          year: cur_date_wervs.year,
          month: cur_date_wervs.month,
          day: 1
        }
      }
      this.$emit("skip_date",a_data);
    },
    nextMonthClick: function () {
      let cur_date_wervs = this.cur_date;
      cur_date_wervs.month= parseInt(cur_date_wervs.month)+1;
      if (cur_date_wervs.month === 13){
        cur_date_wervs.month=1;
        cur_date_wervs.year=cur_date_wervs.year+1;
      }
      this.$refs.monthview.dealMonthViewRequest();
      let a_data = {
        skip_date: {
          year: cur_date_wervs.year,
          month: cur_date_wervs.month,
          day: 1
        }
      }
      this.$emit("skip_date",a_data);
    },
    specificDate: function (){
      let el = document.getElementById("div_body_ljfsdouwerle");
      el.style.display="block";
    },
  },
}
</script>

<style scoped>
#span_body_div_erwevsdcws>*{
  margin: 0px 26px;
}
#span_body_div_erwevsdcwswerwer>span{
  margin: 0px 50px;
}

</style>
